import React from 'react';
import Container from '../../common/Container/Container';
import Typography from '../../common/Typography/Typography';
import Button from '../../common/Button/Button';
import './Banner.scss';

const Banner = ({ banner }) => {
  const { photo, title } = banner;

  const renderSubtitle = banner.subtitle && (
    <Typography variant="p" customClass="banner__subtitle" type="subtitle">
      {banner.subtitle}
    </Typography>
  );

  const renderDescription = banner.description && (
    <Typography variant="p" customClass="banner__description">
      {banner.description}
    </Typography>
  );
  return (
    <Container customClass="banner">
      <img src={photo} alt="slide" className="banner__image" />
      <Container customClass="banner__container">
        {renderSubtitle}

        <Typography variant="h1" customClass="banner__title">
          {title}
        </Typography>
        {renderDescription}
        {console.log(banner)}
        {banner.button && (
          <Container customClass="banner__buttons">
            <Button
              link={banner.button.order.url}
              name={banner.button.order.name}
              variant="primary"
            />
            <Button
              link={banner.button.view.url}
              name={banner.button.view.name}
              variant="secondary"
            />
          </Container>
        )}
      </Container>
    </Container>
  );
};

export default Banner;
