import React from 'react';
import classNames from 'classnames';
import Link from '../Link/Link';

const Button = ({ customClass, name, link, variant, color }) => {
  const buttonClasses = classNames('button', customClass, {
    [`button--${color}`]: color,
    [`button--${variant}`]: variant,
  });

  const renderButton = link ? (
    <Link customClass={buttonClasses} link={link}>
      {name}
    </Link>
  ) : (
    <button customClass={buttonClasses}>{name}</button>
  );
  return renderButton;
};

export default Button;
