import React from 'react';
import { Formik, Field } from 'formik';
import Container from '../../common/Container/Container';
import Typography from '../../common/Typography/Typography';
import './Form.scss';

const Form = () => (
  <Container customClass="form">
    <Typography variant="h2" customClass="form__title">
      BOOK A TABLE
    </Typography>
    <Formik
      initialValues={{
        email: '',
        name: '',
        phone: '',
        date: '',
        time: '',
        guest: '',
      }}
      validate={(values) => {
        const errors = {};
        if (!values.email) {
          errors.email = 'This filed is required';
        } else if (
          !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
        ) {
          errors.email = 'Invalid email address';
        }
        if (values.name && !/^([a-zA-Z ]){2,30}$/i.test(values.name)) {
          errors.name = 'Invalid name';
        }
        if (values.phone && !/\d{9}/.test(values.phone)) {
          errors.phone = 'Invalid phone number (890190290)';
        }
        if (!values.date) {
          errors.date = 'Select your date appointment';
        }
        if (!values.time) {
          errors.time = 'Select your time appointment';
        }
        if (!values.guest) {
          errors.guest = 'Select your table';
        }

        return errors;
      }}
      onSubmit={(values, { setSubmitting }) => {
        setTimeout(() => {
          console.log(values);
          //   alert(JSON.stringify(values, null, 2));
          //   setSubmitting(false);
        }, 400);
      }}
    >
      {({
        values,
        errors,
        touched,
        handleChange,
        handleBlur,
        handleSubmit,
        isSubmitting,
      }) => (
        <>
          <form onSubmit={handleSubmit} className="form__container">
            <Container customClass="form__input">
              <Field
                type="text"
                name="name"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.name}
                placeholder="Name"
              />
              <span className="form__validation">
                {errors.name && touched.name && errors.name}
              </span>
            </Container>
            <Container customClass="form__input">
              <Field
                type="email"
                name="email"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.email}
                placeholder="Email"
              />
              <span className="form__validation">
                {errors.email && touched.email && errors.email}
              </span>
            </Container>
            <Container customClass="form__input">
              <Field
                type="tel"
                name="phone"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.phone}
                placeholder="Phone number"
              />
              <span className="form__validation">
                {errors.phone && touched.phone && errors.phone}
              </span>
            </Container>

            <Container customClass="form__input">
              <Field
                type="date"
                name="date"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.date}
                min="2021-01-01"
                max="2022-12-31"
              />
              <span className="form__validation">
                {errors.date && touched.date && errors.date}
              </span>
            </Container>

            <Container customClass="form__input">
              <Field component="select" id="time" name="time">
                <option value="" disabled>
                  Select your time appointment
                </option>
                <option value="12:00pm">12:00pm</option>
                <option value="01:00pm">01:00pm</option>
                <option value="02:00pm">02:00pm</option>
                <option value="03:00pm">03:00pm</option>
                <option value="06:00pm">06:00pm</option>
                <option value="07:00pm">07:00pm</option>
                <option value="08:00pm">08:00pm</option>
                <option value="09:00pm">09:00pm</option>
                <option value="10:00pm">10:00pm</option>
              </Field>
              <span className="form__validation">
                {errors.time && touched.time && errors.time}
              </span>
            </Container>
            <Container customClass="form__input">
              <Field component="select" id="guest" name="guest">
                <option value="" disabled>
                  Select your table
                </option>
                <option value="2">2 guests</option>
                <option value="4">4 guests</option>
                <option value="6">6 guests</option>
                <option value="8">8 guests</option>
              </Field>
              <span className="form__validation">
                {errors.guest && touched.guest && errors.guest}
              </span>
            </Container>
          </form>
          <button type="submit" disabled={isSubmitting}>
            Appointment
          </button>
        </>
      )}
    </Formik>
  </Container>
);

export default Form;
