import React from 'react';
import Typography from '../../common/Typography/Typography';
import Container from '../../common/Container/Container';
import { story } from '../../content/story';
import banner from '../../asets/banner.jpg';
import './Story.scss';

const Story = () => {
  const { title, subtitle, description } = story;
  return (
    <Container customClass="story">
      <img className="story__image" src={banner} alt="banner" />
      <Container customClass="story__container">
        <Typography variant="h2" type="subtitle">
          {subtitle}
        </Typography>
        <Typography variant="h2" type="title">
          {title}
        </Typography>
        <Typography variant="p">{description}</Typography>
      </Container>
    </Container>
  );
};

export default Story;
