import React from 'react';
import Typography from '../../common/Typography/Typography';

const Heading = ({ info }) => {
  const { title, subtitle, description } = info;
  return (
    <>
      <Typography variant="h2" type="subtitle">
        {subtitle}
      </Typography>
      <Typography variant="h2" type="title">
        {title}
      </Typography>
      <Typography variant="p" type="description">
        {description}
      </Typography>
    </>
  );
};

export default Heading;
