import React from 'react';
import Container from '../../common/Container/Container';
import Typography from '../../common/Typography/Typography';
import classNames from 'classnames';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faTruck,
  faCoffee,
  faFileContract,
} from '@fortawesome/free-solid-svg-icons';
import './Info.scss';

const Info = ({ info, variant, color }) => {
  const renderIcon = (type) => (
    <>
      {type === 'delivery' && <FontAwesomeIcon icon={faTruck} />}
      {type === 'coffee' && <FontAwesomeIcon icon={faCoffee} />}
      {type === 'order' && <FontAwesomeIcon icon={faFileContract} />}
    </>
  );

  const infoClasses = classNames('info', {
    [`info--${variant}`]: variant,
  });

  const colorClasses = classNames('info', {
    [`${color}`]: color,
  });

  return (
    <Container customClass={infoClasses}>
      {info.map((item, index) => {
        const { name, title, description } = item;
        return (
          <Container customClass="info__item" key={index}>
            <Container customClass="info__icon">{renderIcon(name)}</Container>
            <Typography variant="h2" customClass="info__title" color="black">
              {title}
            </Typography>
            <Typography
              variant="p"
              customClass="info__description"
              color="black"
              type="description"
            >
              {description}
            </Typography>
          </Container>
        );
      })}
    </Container>
  );
};

export default Info;
