import React from 'react';
import Container from '../../common/Container/Container';
import Typography from '../../common/Typography/Typography';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faTruck,
  faCoffee,
  faFileContract,
} from '@fortawesome/free-solid-svg-icons';
import './Services.scss';

const Services = ({ services }) => {
  console.log(services[0].name);
  const { name } = services;
  const renderIcon = () => {
    return (
      <>
        {name === 'delivery' && <FontAwesomeIcon icon={faTruck} />}
        {name === 'coffee' && <FontAwesomeIcon icon={faCoffee} />}
        {name === 'order' && <FontAwesomeIcon icon={faFileContract} />}
      </>
    );
  };
  return (
    <Container className="services">
      {services.map((item) => (
        <Container customClass="services__item">
          <Container customClass="services__icon">{renderIcon()}</Container>
          <Typography variant="h2">{item.title}</Typography>
          <Typography variant="p">{item.description}</Typography>
        </Container>
      ))}
    </Container>
  );
};

export default Services;
