import React from 'react';
import Container from '../../common/Container/Container';
import Typography from '../../common/Typography/Typography';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faTruck,
  faCoffee,
  faFileContract,
} from '@fortawesome/free-solid-svg-icons';
import './Info.scss';

const Info = ({ item, color }) => {
  const { title, name, description } = item;
  const renderIcon = (type) => {
    return (
      <>
        {type === 'delivery' && <FontAwesomeIcon icon={faTruck} />}
        {type === 'coffee' && <FontAwesomeIcon icon={faCoffee} />}
        {type === 'order' && <FontAwesomeIcon icon={faFileContract} />}
      </>
    );
  };
  return (
    <Container customClass="services__item">
      <Container customClass="services__icon">{renderIcon(name)}</Container>
      <Typography variant="h2" customClass="services__title" color={color}>
        {title}
      </Typography>
      <Typography
        variant="p"
        customClass="services__description"
        color={color}
        type="description"
      >
        {description}
      </Typography>
    </Container>
  );
};

export default Info;
