import React from 'react';
import Container from '../../common/Container/Container';
import Typography from '../../common/Typography/Typography';
import slide1 from '../../asets/slide-1.webp';
import slide2 from '../../asets/slide-2.webp';
import slide3 from '../../asets/slide-3.webp';
import Link from '../../common/Link/Link';
import './Banner.scss';
import Button from '../../common/Button/Button';

const Banner = ({ slide, index }) => {
  const {
    title,
    subtitle,
    description,
    buttons: { order, view },
  } = slide;
  const imageSlides = [slide1, slide2, slide3];
  return (
    <Container customClass="banner">
      <img src={imageSlides[index]} alt="slide" className="banner__image" />
      <Container customClass="banner__container">
        <Typography variant="p" customClass="banner__subtitle">
          {subtitle}
        </Typography>
        <Typography variant="h1" customClass="banner__title">
          {title}
        </Typography>
        <Typography variant="p" customClass="banner__description">
          {description}
        </Typography>
        <Container customClass="banner__buttons">
          <Button />
          <button className="banner__button banner__button--order">
            <Link link={order.url} customClass="banner__link">
              {order.name}
            </Link>
          </button>
          <button className="banner__button banner__button--view">
            <Link link={view.url} customClass="banner__link">
              {view.name}
            </Link>
          </button>
        </Container>
      </Container>
    </Container>
  );
};

export default Banner;
