import React from 'react';
import Container from '../../common/Container/Container';
import Typography from '../../common/Typography/Typography';
import { footer } from '../../content/footer';

const FooterTest = () => {
  const renderSocials = socials.map((social) => {
    const { name, link } = social;
    return (
      <Link link={link} key={name}>
        <Icon name={name} />
      </Link>
    );
  });
  return (
    <footer className="footer-test">
      {footer.map((item) => {
        const { title, type } = item;
        return (
          <Container customClass="footer-test__container">
            <Typography variant="h3" customClass="footer__title">
              {title}
            </Typography>
            {type === 'about' && (
              <>
                <Typography
                  variant="p"
                  customClass="footer__description"
                  color="grey"
                  type="description"
                >
                  {description}
                </Typography>
                <Container customClass="footer__socials">
                  {renderSocials}
                </Container>
              </>
            )}
          </Container>
        );
      })}
    </footer>
  );
};

export default FooterTest;
