import React from 'react';
import Container from '../../common/Container/Container';
import Typography from '../../common/Typography/Typography';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faEnvelope,
  faPhoneAlt,
  faMapMarkedAlt,
  faClock,
} from '@fortawesome/free-solid-svg-icons';
import { details } from '../../content/details';
import './Details.scss';

const Details = () => (
  <Container customClass="details">
    {details.map((detail) => {
      const { name, description } = detail;
      return (
        <Container customClass="detail" key={name}>
          {name === 'map' && <FontAwesomeIcon icon={faMapMarkedAlt} />}
          {name === 'phone' && <FontAwesomeIcon icon={faPhoneAlt} />}
          {name === 'email' && <FontAwesomeIcon icon={faEnvelope} />}
          {name === 'hours' && <FontAwesomeIcon icon={faClock} />}
          <Typography
            variant="p"
            customClass="detail__description"
            color="grey"
            type="description"
          >
            {description}
          </Typography>
        </Container>
      );
    })}
  </Container>
);

export default Details;
