import React from 'react';
import classNames from 'classnames';
import Container from '../../common/Container/Container';
import Typography from '../../common/Typography/Typography';
import image1 from '../../asets/coffee-1.jpg';
import image2 from '../../asets/coffee-2.webp';
import image3 from '../../asets/coffee-3.webp';
import image4 from '../../asets/coffee-4.webp';
import './Products.scss';

const Products = ({ products, variant }) => {
  const images = [image1, image2, image3, image4];

  const productClasses = classNames('product', {
    [`typography--${variant}`]: variant,
  });

  return (
    <Container customClass="products">
      {products.map((item, index) => {
        const { name, description, price } = item;
        return (
          <Container customClass={productClasses}>
            <img className="product__image" src={images[index]} alt="coffee" />
            <Typography
              customClass="product__name"
              variant="h3"
              align="center"
              type="name"
            >
              {name}
            </Typography>
            <Typography
              customClass="product__description"
              variant="p"
              align="center"
              type="description"
            >
              {description}
            </Typography>
            <Typography
              customClass="product__price"
              variant="p"
              align="center"
              type="name"
            >
              {price}
            </Typography>
          </Container>
        );
      })}
    </Container>
  );
};

export default Products;
