import React, { useState } from 'react';
import classNames from 'classnames';
import { Formik, Field } from 'formik';
import Container from '../../common/Container/Container';
import Typography from '../../common/Typography/Typography';
import Button from '../../common/Button/Button';
import './Form.scss';

const Form = ({ btnVariant }) => {
  const [validationPassed, setValidationPassed] = useState(false);
  const [bookTable, setBookTable] = useState();

  const firstStageClasses = classNames('form__container', {
    'form__container--hide': validationPassed,
  });
  const succesStageClasses = classNames('form__success', {
    'form__success--active': validationPassed,
  });

  if (typeof window !== 'undefined') {
    const sessionData = JSON.parse(
      sessionStorage.getItem('reservation_coffee_blend'),
    );

    if (sessionData) {
      console.log(sessionData);
      setBookTable(sessionData);
    }
  }

  return (
    <Container customClass="form">
      <Typography variant="h2" customClass="form__title">
        {validationPassed ? "WE CAN'T WAIT FOR YOU" : 'BOOK A TABLE'}
      </Typography>
      <Formik
        initialValues={{
          email: '',
          name: '',
          phone: '',
          date: '',
          time: '',
          guest: '',
        }}
        validate={(values) => {
          const errors = {};
          if (!values.email) {
            errors.email = 'This filed is required';
          } else if (
            !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
          ) {
            errors.email = 'Invalid email address';
          }
          if (values.name && !/^([a-zA-Z ]){2,30}$/i.test(values.name)) {
            errors.name = 'Invalid name';
          }
          if (values.phone && !/\d{9}/.test(values.phone)) {
            errors.phone = 'Invalid phone number';
          }
          if (!values.date) {
            errors.date = 'Select date appointment';
          }
          if (!values.time) {
            errors.time = 'Select time appointment';
          }
          if (!values.guest) {
            errors.guest = 'Select your table';
          }

          return errors;
        }}
        onSubmit={(values, { setSubmitting }) => {
          setTimeout(() => {
            setBookTable(values);
            setValidationPassed(true);
            if (typeof window !== 'undefined') {
              sessionStorage.setItem('reservation_coffee_blend', bookTable);
            }
          }, 400);
        }}
      >
        {({
          values,
          errors,
          touched,
          handleChange,
          handleBlur,
          handleSubmit,
          isSubmitting,
        }) => (
          <form onSubmit={handleSubmit} className={firstStageClasses}>
            <Container customClass="form__input">
              <Field
                type="text"
                name="name"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.name}
                placeholder="Name"
              />
              <span className="form__validation">
                {errors.name && touched.name && errors.name}
              </span>
            </Container>
            <Container customClass="form__input">
              <Field
                type="email"
                name="email"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.email}
                placeholder="Email"
              />
              <span className="form__validation">
                {errors.email && touched.email && errors.email}
              </span>
            </Container>
            <Container customClass="form__input">
              <Field
                type="tel"
                name="phone"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.phone}
                placeholder="Number"
              />
              <span className="form__validation">
                {errors.phone && touched.phone && errors.phone}
              </span>
            </Container>

            <Container customClass="form__input">
              <Field
                type="date"
                name="date"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.date}
                min="2021-01-01"
                max="2022-12-31"
              />
              <span className="form__validation">
                {errors.date && touched.date && errors.date}
              </span>
            </Container>

            <Container customClass="form__input">
              <Field component="select" name="time">
                <option value="" disabled>
                  Select time appointment
                </option>
                <option value="12:00pm">12:00pm</option>
                <option value="01:00pm">01:00pm</option>
                <option value="02:00pm">02:00pm</option>
                <option value="03:00pm">03:00pm</option>
                <option value="06:00pm">06:00pm</option>
                <option value="07:00pm">07:00pm</option>
                <option value="08:00pm">08:00pm</option>
                <option value="09:00pm">09:00pm</option>
                <option value="10:00pm">10:00pm</option>
              </Field>
              <span className="form__validation">
                {errors.time && touched.time && errors.time}
              </span>
            </Container>
            <Container customClass="form__input">
              <Field component="select" name="guest">
                <option value="" disabled>
                  Select table
                </option>
                <option value="2">2 guests</option>
                <option value="4">4 guests</option>
                <option value="6">6 guests</option>
                <option value="8">8 guests</option>
              </Field>
              <span className="form__validation">
                {errors.guest && touched.guest && errors.guest}
              </span>
            </Container>
            <Button
              name="Appointment"
              type="submit"
              disabled={isSubmitting}
              variant={btnVariant ? btnVariant : 'primary'}
              customClass="form__button"
            />
          </form>
        )}
      </Formik>
      <Container customClass={succesStageClasses}>
        <Typography variant="p" customClass="form__description">
          {bookTable &&
            `Your table for ${bookTable.guest} quests will be ready ${bookTable.date} at ${bookTable.time} `}
        </Typography>
        <Typography variant="p" customClass="form__description" align="center">
          See you!
        </Typography>
      </Container>
    </Container>
  );
};

export default Form;
