import React from 'react';
import Container from '../../common/Container/Container';
import Heading from '../Heading/Heading';
import { story } from '../../content/story';
import banner from '../../asets/banner.jpg';
import './DiscoverStory.scss';

const DiscoverStory = () => (
  <Container customClass="discover-story">
    <img className="discover-story__image" src={banner} alt="banner" />
    <Container customClass="discover-story__container">
      <Heading info={story} align="left" />
    </Container>
  </Container>
);

export default DiscoverStory;
