import React, { useRef } from 'react';
import Container from '../../common/Container/Container';
import Button from '../../common/Button/Button';
import './Filter.scss';

const Filter = ({ setItems, products }) => {
  const buttonElement = useRef(null);
  const filteredTypes = products
    .map((product) => product.type)
    .reduce((acc, item) => {
      !acc.includes(item) && acc.push(item);
      return acc;
    }, []);

  const filteredProducts = (type, products) =>
    products.filter((product) => product.type === type);

  const handleClick = (event) =>
    setItems(filteredProducts(event.target.id, products));

  const renderButtons = filteredTypes.map((item) => {
    return (
      <Button
        ref={buttonElement}
        id={item}
        onClick={handleClick}
        customClass="filter__button"
        name={item}
        variant="filter"
        key={item}
      />
    );
  });

  return <Container customClass="filter">{renderButtons}</Container>;
};

export default Filter;
