import React from 'react';
import Container from '../../common/Container/Container';
import Typography from '../../common/Typography/Typography';
import './Info.scss';

const Info = ({ item }) => {
  const { title, name, description } = item;
  return (
    <Container customClass="services__item">
      <Container customClass="services__icon">{renderIcon(name)}</Container>
      <Typography variant="h2" customClass="services__title" color="black">
        {title}
      </Typography>
      <Typography
        variant="p"
        customClass="services__description"
        color="black"
        type="description"
      >
        {description}
      </Typography>
    </Container>
  );
};

export default Info;
