import React from 'react';
import Container from '../../common/Container/Container';
import Link from '../../common/Link/Link';
import Typography from '../../common/Typography/Typography';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faFacebook,
  faInstagram,
  faTwitter,
} from '@fortawesome/free-brands-svg-icons';

import '../Footer/Footer.scss';

const FooterAbout = ({ content }) => {
  const { title, description, socials } = content;
  const renderSocials = socials.map((socialName) => {
    return (
      <Link link="/sdas" key={socialName} alt={socialName}>
        {socialName === 'facebook' && <FontAwesomeIcon icon={faFacebook} />}
        {socialName === 'twitter' && <FontAwesomeIcon icon={faTwitter} />}
        {socialName === 'instagram' && <FontAwesomeIcon icon={faInstagram} />}
      </Link>
    );
  });
  return (
    <>
      <Typography variant="h3" customClass="footer__title">
        {title}
      </Typography>
      <Typography
        variant="p"
        customClass="footer__description"
        color="grey"
        type="description"
      >
        {description}
      </Typography>
      <Container customClass="footer__socials">{renderSocials}</Container>
    </>
  );
};

export default FooterAbout;
