import React from 'react';
import { Formik } from 'formik';
import Container from '../../common/Container/Container';
import Typography from '../../common/Typography/Typography';

const Form = () => (
  <Container customClass="form">
    <Typography variant="h2">Anywhere in your app!</Typography>
    <Formik
      initialValues={{ email: '', name: '', phone: '' }}
      validate={(values) => {
        const errors = {};
        if (!values.email) {
          errors.email = 'Required';
        } else if (
          !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
        ) {
          errors.email = 'Invalid email address';
        }
        if (values.name && !/^([a-zA-Z ]){2,30}$/i.test(values.name)) {
          errors.name = 'Invalid name';
        }
        if (values.phone && !/\d{9}/.test(values.phone)) {
          errors.phone = 'Invalid phone number (890190290)';
        }
        return errors;
      }}
      onSubmit={(values, { setSubmitting }) => {
        setTimeout(() => {
          alert(JSON.stringify(values, null, 2));
          setSubmitting(false);
        }, 400);
      }}
    >
      {({
        values,
        errors,
        touched,
        handleChange,
        handleBlur,
        handleSubmit,
        isSubmitting,
        /* and other goodies */
      }) => (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            onChange={handleChange}
            onBlur={handleBlur}
            value={values.name}
            placeholder="name"
          />
          {errors.name && touched.name && errors.name}
          <input
            type="email"
            name="email"
            onChange={handleChange}
            onBlur={handleBlur}
            value={values.email}
            placeholder="email"
          />
          {errors.email && touched.email && errors.email}
          <input
            type="number"
            name="phone"
            onChange={handleChange}
            onBlur={handleBlur}
            value={values.phone}
            placeholder="phone"
          />
          <button type="submit" disabled={isSubmitting}>
            Submit
          </button>
        </form>
      )}
    </Formik>
  </Container>
);

export default Form;
