import React from 'react';
import Container from '../../common/Container/Container';
import Link from '../../common/Link/Link';
import Typography from '../../common/Typography/Typography';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faFacebook,
  faInstagram,
  faTwitter,
} from '@fortawesome/free-brands-svg-icons';

import '../../containers/Footer/Footer.scss';

const FooterAbout = ({ content }) => {
  const { title, description, socials } = content;
  const renderSocials = socials.map((social) => {
    const { name, link } = social;
    return (
      <Link link={link} key={name}>
        {name === 'facebook' && <FontAwesomeIcon icon={faFacebook} />}
        {name === 'twitter' && <FontAwesomeIcon icon={faTwitter} />}
        {name === 'instagram' && <FontAwesomeIcon icon={faInstagram} />}
      </Link>
    );
  });
  return (
    <>
      <Typography variant="h3" customClass="footer__title">
        {title}
      </Typography>
      <Typography
        variant="p"
        customClass="footer__description"
        color="grey"
        type="description"
      >
        {description}
      </Typography>
      <Container customClass="footer__socials">{renderSocials}</Container>
    </>
  );
};

export default FooterAbout;
