import React from 'react';
import Container from '../../common/Container/Container';
import Typography from '../../common/Typography/Typography';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faEnvelope,
  faPhone,
  faMapMarkedAlt,
} from '@fortawesome/free-solid-svg-icons';

import '../Footer/Footer.scss';

const FooterInfo = ({ info }) => {
  const { title, details } = info;

  const renderDetails = details.map((detail) => {
    const { name, description } = detail;
    return (
      <Container customClass="footer__details" key={name}>
        <FontAwesomeIcon icon={faEnvelope} />
        {name === 'map' && <FontAwesomeIcon icon={faMapMarkedAlt} />}
        {name === 'phone' && <FontAwesomeIcon icon={faPhone} />}
        {name === 'email' && <FontAwesomeIcon icon={faEnvelope} />}
        <Typography variant="p" customClass="footer__description">
          {description}
        </Typography>
      </Container>
    );
  });
  return (
    <>
      <Typography variant="h3" customClass="footer__title">
        {title}
      </Typography>
      {renderDetails}
    </>
  );
};

export default FooterInfo;
