import React from 'react';
import Container from '../../common/Container/Container';
import Typography from '../../common/Typography/Typography';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faTruck,
  faCoffee,
  faFileContract,
} from '@fortawesome/free-solid-svg-icons';
import './Services.scss';

const Services = ({ services }) => {
  const renderIcon = (type) => {
    console.log(type);
    return (
      <>
        {type === 'delivery' && <FontAwesomeIcon icon={faTruck} />}
        {type === 'coffee' && <FontAwesomeIcon icon={faCoffee} />}
        {type === 'order' && <FontAwesomeIcon icon={faFileContract} />}
      </>
    );
  };
  return (
    <Container customClass="services">
      {services.map((item) => {
        const { name, title, description } = item;
        return (
          <Container customClass="services__item">
            <Container customClass="services__icon">
              {renderIcon(name)}
            </Container>
            <Typography
              variant="h3"
              customClass="services__title"
              color="black"
            >
              {title}
            </Typography>
            <Typography
              variant="p"
              customClass="services__description"
              color="black"
            >
              {description}
            </Typography>
          </Container>
        );
      })}
    </Container>
  );
};

export default Services;
