import React, { useState } from 'react';
import Container from '../../common/Container/Container';
import Heading from '../Heading/Heading';
import Products from '../Products/Products';
import { menu } from '../../content/menu';
import { MAIN_DISHES } from '../Filter/constants';
import './Menu.scss';
import Filter from '../Filter/Filter';

const Menu = () => {
  const { heading, products } = menu;

  const filteredProducts = (type, products) =>
    products.filter((product) => product.type === type);

  const [items, setItems] = useState(filteredProducts(MAIN_DISHES, products));

  return (
    <Container customClass="menu">
      <Heading info={heading} align="center" />
      <Filter products={products} setItems={setItems} />
      <Products products={items} variant="row-3" />
    </Container>
  );
};

export default Menu;
