import React from 'react';
import slide1 from '../../asets/slide-1.webp';
import slide2 from '../../asets/slide-2.webp';
import slide3 from '../../asets/slide-3.webp';
import './Banner.scss';

const Banner = ({ homepage }) => {
  const imageSlides = [slide1, slide2, slide3];
  console.log(homepage);
  return homepage.map((item, index) => {
    const {
      title,
      subtitle,
      description,
      buttons: { order, view },
    } = item;
    return (
      <div className="banner">
        <img src={imageSlides[index]} alt="slide" />
        <h1 className="banner__subtitle">{subtitle}</h1>
        <h1 className="banner__title">{title}</h1>
        <p className="description">{description}</p>
        <div className="banner__buttons">
          <button className="banner__button banner__button--order">
            {order.name}
          </button>
          <button className="banner__button banner__button--view">
            {view.name}
          </button>
        </div>
      </div>
    );
  });
};

export default Banner;
