import React from 'react';
import Container from '../../common/Container/Container';
import Typography from '../../common/Typography/Typography';
import Button from '../../common/Button/Button';
import slide1 from '../../asets/slide/slide-1.webp';
import slide2 from '../../asets/slide/slide-2.webp';
import slide3 from '../../asets/slide/slide-3.webp';
import './Banner.scss';

const Banner = ({ slide, index }) => {
  const {
    title,
    subtitle,
    description,
    buttons: { order, view },
  } = slide;
  const imageSlides = [slide1, slide2, slide3];
  return (
    <Container customClass="banner">
      <img src={imageSlides[index]} alt="slide" className="banner__image" />
      <Container customClass="banner__container">
        <Typography variant="p" customClass="banner__subtitle" type="subtitle">
          {subtitle}
        </Typography>
        <Typography variant="h1" customClass="banner__title">
          {title}
        </Typography>
        <Typography variant="p" customClass="banner__description">
          {description}
        </Typography>
        <Container customClass="banner__buttons">
          <Button link={order.url} name={order.name} variant="primary" />
          <Button link={view.url} name={view.name} variant="secondary" />
        </Container>
      </Container>
    </Container>
  );
};

export default Banner;
