import React from 'react';
import Slider from 'react-slick';
import slide1 from '../../asets/slide-1.webp';
import slide2 from '../../asets/slide-2.webp';
import slide3 from '../../asets/slide-3.webp';
import Banner from '../Banner/';

const MainSlider = ({ homepage }) => {
  const imageSlides = [slide1, slide2, slide3];
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  return homepage.map((slide, index) => (
    <Slider>
      <Banner slide={slide} imageSlides={imageSlides[index]} />
    </Slider>
  ));
};

export default MainSlider;
