import React, { useEffect, useState, useRef } from 'react';
import classNames from 'classnames';
import Fade from 'react-reveal/Fade';
import Container from '../../common/Container/Container';
import Typography from '../../common/Typography/Typography';
import Icon from '../../common/Icon/Icon';
import './Info.scss';

const Info = ({ info, variant, colorTitle, colorDescription }) => {
  const [scrollTop, setScrollTop] = useState(0);
  const renderIcon = (type) => <Icon name={type} />;

  const infoClasses = classNames('info', {
    [`info--${variant}`]: variant,
  });

  const infoAnimationClasses = classNames('info__title', {
    [`info__animation--${variant}`]: variant,
  });

  const isBrowser = () => typeof window !== 'undefined';
  const blockRefs = useRef([null]);

  useEffect(() => {
    if (!isBrowser()) return;

    function onScroll() {
      const currentPosition = window.scrollY;
      const containerPosition = document.querySelector('#container-animation');
      console.log(blockRefs.current[2].innerHTML);
      containerPosition.offsetTop < currentPosition &&
        blockRefs.current.map((item) => {
          const value = item.getAttribute('data-count');
          let divValue = parseInt(item.innerHTML);
          const initialValue = divValue;
          return divValue <= value && (divValue = divValue + 1);
        });

      setScrollTop(currentPosition);
    }
    window.addEventListener('scroll', onScroll);

    return () => window.removeEventListener('scroll', onScroll);

    // const getElements = document.querySelectorAll('.info__animation--image');
    // const getPositionElements = getElements[0].offsetTop;
  }, [scrollTop]);

  return (
    <Container customClass={infoClasses} id={variant && 'container-animation'}>
      {info.map((item, index) => {
        const { name, title, description } = item;
        return (
          <Fade bottom key={index}>
            <Container customClass="info__item">
              <Container customClass="info__icon">{renderIcon(name)}</Container>
              <Typography
                variant="h2"
                customClass={infoAnimationClasses}
                color={colorTitle}
                dataCount={variant && title}
                innerRef={(element) => {
                  blockRefs.current[index] = element;
                }}
              >
                3
              </Typography>
              <Typography
                variant="p"
                customClass="info__description"
                color={colorDescription}
                type="description"
              >
                {description}
              </Typography>
            </Container>
          </Fade>
        );
      })}
    </Container>
  );
};

export default Info;
