import React from 'react';
import Container from '../../common/Container/Container';
import Typography from '../../common/Typography/Typography';
import image1 from '../../asets/coffee-1.jpg';
import image2 from '../../asets/coffee-2.webp';
import image3 from '../../asets/coffee-3.webp';
import image4 from '../../asets/coffee-4.webp';
import './Products.scss';

const Products = ({ products }) => {
  const images = [image1, image2, image3, image4];
  return (
    <Container customClass="products">
      {products.map((item, index) => {
        const { name, description, price } = item;
        return (
          <Container customClass="product">
            <img className="product__image" src={images[index]} alt="coffee" />
            <Typography variant="h3" align="center">
              {name}
            </Typography>
            <Typography variant="p" align="center">
              {description}
            </Typography>
            <Typography variant="p" align="center">
              {price}
            </Typography>
          </Container>
        );
      })}
    </Container>
  );
};

export default Products;
