import React from 'react';
import Container from '../../common/Container/Container';
import Heading from '../Heading/Heading';
import Button from '../../common/Button/Button';
import { discoverMenu } from '../../content/discover-menu';
import image1 from '../../asets/coffees/coffee-1.jpg';
import image2 from '../../asets/coffees/coffee-2.webp';
import image3 from '../../asets/coffees/coffee-3.webp';
import image4 from '../../asets/coffees/coffee-4.webp';
import './DiscoverMenu.scss';

const DiscoverMenu = () => {
  const images = [image1, image2, image3, image4];
  const {
    button: { name, url },
  } = discoverMenu;
  return (
    <Container customClass="discover-menu">
      <Container customClass="discover-menu__container">
        <Heading info={discoverMenu} align="left" />
        <Button
          link={url}
          name={name}
          variant="tertiary"
          customClass="discover-menu__button"
        />
      </Container>
      <Container customClass="discover-menu__images">
        {images.map((item, index) => (
          <img
            src={item}
            key={index}
            className="discover-menu__image"
            alt="coffee"
          />
        ))}
      </Container>
    </Container>
  );
};

export default DiscoverMenu;
