import React from 'react';
import classNames from 'classnames';

const Container = ({ customClass, children, variant }) => {
  const Components = variant ? 'section' : 'div';
  const containerClasses = classNames(customClass, {
    [`container--section`]: variant,
  });
  return <Components className={containerClasses}>{children}</Components>;
};

export default Container;
