import React from 'react';
import Container from '../../common/Container/Container';
import Typography from '../../common/Typography/Typography';
import Button from '../../common/Button/Button';

import './Banner.scss';

const Banner = ({ banner }) => {
  const { photo, title, buttons, subtitle, description } = banner;

  const renderSubtitle = subtitle && (
    <Typography variant="p" customClass="banner__subtitle" type="subtitle">
      {banner.subtitle}
    </Typography>
  );

  const renderDescription = description && (
    <Typography variant="p" customClass="banner__description">
      {banner.description}
    </Typography>
  );

  const renderButtons = buttons && (
    <Container customClass="banner__buttons">
      {banner.buttons.map((button) => {
        const { name, url, variant } = button;
        return <Button key={name} link={url} name={name} variant={variant} />;
      })}
    </Container>
  );

  return (
    <Container customClass="banner">
      <Image image={photo} alt="slide" customClass="banner__image" />
      <Container customClass="banner__container">
        {renderSubtitle}
        <Typography variant="h1" customClass="banner__title">
          {title}
        </Typography>
        {renderDescription}
        {renderButtons}
      </Container>
    </Container>
  );
};

export default Banner;
